func (e htmlElement[T]) Styles(s map[string]string) T {
	var b strings.Builder
	for k, v := range s {
		b.WriteString(k)
		b.WriteByte(':')
		b.WriteString(v)
		b.WriteByte(';')
	}
	return e.Attr("style", strings.Trim(b.String(), ";"))
}

